mapper.select:
select  T.ID			as id,
		T.FILE_ID   	as fileId,
		T.TRAIN_RES 	as trainRes,
		T.ACCURACY	 	as accuracy,
		T.TRAIN_NO	  	as trainNo,
		T.TEST_NO   	as testNo,
		T.PERFORMANCE   as performance,
		T.CREATE_APP    as createApp,
		T.CREATE_TIME   as uploadUser,
from T_PREDICT_INDEX T
1=1

     and T.FILE_ID = #{condition.fileId} 
     and T.TRAIN_RES like '%${condition.trainRes}%' 
     and T.ACCURACY >= #{condition.accurMin} 
     and T.ACCURACY <= #{condition.accurMax} 
     and T.TRAIN_NO >= #{condition.trainMin}  
     and T.TRAIN_NO <= #{condition.trainMax} 
     and T.TEST_NO >= #{condition.testMin} 
     and T.TEST_NO <= #{condition.testMax} 
     and T.CREATE_TIME >= #{condition.crtStartTime} 
     and T.CREATE_TIME <= #{condition.crtEndTime} 
     and T.CREATE_APP like '%${condition.createApp}%' 

 ORDER BY ${condition.orders}
